package com.khmelenko.lab.varisimport com.khmelenko.lab.varis.util.EncryptionUtilsimport org.junit.Testimport org.junit.runner.RunWithimport org.robolectric.RobolectricTestRunnerimport org.robolectric.annotation.Configimport junit.framework.Assert.assertEquals/** * Test for enctyption utils * * @author Dmytro Khmelenko (d.khmelenko@gmail.com) */@RunWith(RobolectricTestRunner::class)@Config(sdk = [21])class TestEncryptionUtils {    @Test    fun testToBase64() {        val original = "Hello world"        val expected = "SGVsbG8gd29ybGQ="        val actual = EncryptionUtils.toBase64(original)        assertEquals(expected, actual)    }    @Test    fun testFromBase64() {        val original = "SGVsbG8gd29ybGQ="        val expected = "Hello world"        val actual = EncryptionUtils.fromBase64(original)        assertEquals(expected, actual)    }    @Test    fun testBasicAuth() {        val username = "username"        val password = "password"        val authBase64 = "dXNlcm5hbWU6cGFzc3dvcmQ="        val expected = "Basic $authBase64"        val actual = EncryptionUtils.generateBasicAuthorization(username, password)        assertEquals(expected, actual)    }}